Change Parameter through Keyboard.update:
for (var keyCode in pins.keyMap) {
  if (pins.keysDown[keyCode]) {
    var value = pins.keyMap[keyCode];
    if (_.isString(value) && value.length > 0 && (value[0] == '+' || value[0] == '-')) {
      pins.parameter += Number(value);
    } else {
      pins.parameter = value;
    }
    break;
  }
}

Detect Mouse.update:
if (!pins.state)
  pins.state = 'none';
switch (pins.state) {
case 'none':
  if (pins.mousePosition) {
    for (var i in pins.shapes) {
      if (transformers.pointIntersectsShape(pins.mousePosition, pins.shapes[i])) {
        log(GE.logLevels.INFO, 'Entering hover mode. Old state = ' + pins.state);
        pins.state = 'hover';
        pins.shape = pins.shapes[i];
        break;
      }
    }
  }
  break;
case 'hover':
  if (!pins.mousePosition || !transformers.pointIntersectsShape(pins.mousePosition, pins.shape)) {
    pins.state = 'none';
    pins.shape = null;
    log(GE.logLevels.INFO, 'Leaving hover mode');
  } else if (pins.mouseDown) {
    pins.dragStartPosition = pins.mousePosition;
    pins.state = 'pressed';
    log(GE.logLevels.INFO, 'Entering presed mode');
  }
  break;
case 'pressed':
  if (!pins.mouseDown) {
    pins.state = 'click';
    pins.dragStartPosition = null;
    log(GE.logLevels.INFO, 'Leaving pressed mode');
  } else if (Vector.create(pins.dragStartPosition).distanceFrom(Vector.create(pins.mousePosition)) >= pins.minimumDragDistance) {
    pins.state = 'startDrag';
    pins.dragStartPosition = null;
    log(GE.logLevels.INFO, 'Entering drag mode');
  }
  break;
case 'click':
  pins.state = 'hover';
  break;
case 'startDrag':
  pins.state = 'drag';
  break;
case 'drag':
  if (!pins.mouseDown) {
    pins.state = 'endDrag';
    pins.dragStartPosition = null;
    log(GE.logLevels.INFO, 'Leaving drag mode');
  }
  break;
case 'endDrag':
  pins.state = 'hover';
  break;
default:
  throw new Error('Unknown state \'' + pins.state + '\'');
}

Do In Sequence.handleSignals:
if (signals[pins.activeChild] == GE.signals.DONE)
  pins.activeChild++;
if (pins.activeChild > children.length - 1) {
  pins.activeChild = 0;
