coordinatesToString.body:
var x = coordinates[0] || coordinates.x;
var y = coordinates[1] || coordinates.y;
return '[' + x + ', ' + y + ']';

pieceToString.body:
if (piece)
  return '{col:' + piece.col + ', row:' + piece.row + ', type:' + piece.type + '}';
else
  return piece;

piecesToString.body:
var printed = '{';
for (var i in pieces) {
  var piece = pieces[i];
  if (printed !== '{') {
    printed += ', ';
  }
  printed += transformers.pieceToString(piece);
}
printed += '}';
return printed;

distance.body:
var point1x = point1[0] || point1.x;
var point1y = point1[1] || point1.y;
var point2x = point2[0] || point2.x;
var point2y = point2[1] || point2.y;
var diffX = point1x - point2x;
var diffY = point1y - point2y;
return Math.sqrt(diffX * diffX + diffY * diffY);

calculateRotationAngle.body:
var h = [
    mousePosition[0] - center[0],
    mousePosition[1] - center[1]
  ];
var omDistance = Math.sqrt(h[0] * h[0] + h[1] * h[1]);
var ratio = -h[1] / omDistance;
var angle = 0;
if (omDistance !== 0) {
  var absValueAngle = Math.acos(ratio) * 180 / Math.PI;
  if (h[0] <= 0) {
    angle = -absValueAngle;
  } else {
    angle = absValueAngle;
  }
}
return angle;
