Detect Mouse.update:
if (!pins.state)
  pins.state = 'none';
switch (pins.state) {
case 'none':
  if (pins.mousePosition) {
    for (var i in pins.shapes) {
      if (transformers.pointIntersectsShape(pins.mousePosition, pins.shapes[i])) {
        log(RW.logLevels.INFO, 'Entering hover mode. Old state = ' + pins.state);
        pins.state = 'hover';
        pins.shape = pins.shapes[i];
        break;
      }
    }
  }
  break;
case 'hover':
  if (!pins.mousePosition || !transformers.pointIntersectsShape(pins.mousePosition, pins.shape)) {
    pins.state = 'none';
    pins.shape = null;
    log(RW.logLevels.INFO, 'Leaving hover mode');
  } else if (pins.mouseDown) {
    pins.dragStartPosition = pins.mousePosition;
    pins.state = 'pressed';
    log(RW.logLevels.INFO, 'Entering presed mode');
  }
  break;
case 'pressed':
  if (!pins.mouseDown) {
    pins.state = 'click';
    pins.dragStartPosition = null;
    log(RW.logLevels.INFO, 'Leaving pressed mode');
  } else if (Vector.create(pins.dragStartPosition).distanceFrom(Vector.create(pins.mousePosition)) >= pins.minimumDragDistance) {
    pins.state = 'startDrag';
    pins.dragStartPosition = null;
    log(RW.logLevels.INFO, 'Entering drag mode');
  }
  break;
case 'click':
  pins.state = 'hover';
  break;
case 'startDrag':
  pins.state = 'drag';
  break;
case 'drag':
  if (!pins.mouseDown) {
    pins.state = 'endDrag';
    pins.dragStartPosition = null;
    log(RW.logLevels.INFO, 'Leaving drag mode');
  }
  break;
case 'endDrag':
  pins.state = 'hover';
  break;
default:
  throw new Error('Unknown state \'' + pins.state + '\'');
}

CollideBall.update:
//test collision with X walls
//TODO end point
var diffX1 = pins.ballPosition.x - pins.walls.x1;
var diffX2 = pins.ballPosition.x - pins.walls.x2;

pins.player1LostPoint = false;
pins.player2LostPoint = false;

//LEFT WALL COLLISION
if(Math.abs(diffX1) < pins.ballRadius || pins.ballPosition.x < pins.walls.x1)//  &&
    //!transformers.isSameSign(diffX1, pins.ballMovement.x)
{ 
  pins.player1LostPoint = true;
  /*   
  pins.ballPosition = {
    x: pins.ballPosition.x + Math.abs(pins.ballMovement.x),
    y: pins.ballPosition.y + pins.ballMovement.y
  };
  pins.ballMovement = {
      x: Math.abs(pins.ballMovement.x),
      y: pins.ballMovement.y
  };
  */
}
else
{
  //RIGHT WALL COLLISION
  if(Math.abs(diffX2) < pins.ballRadius || pins.ballPosition.x > pins.walls.x2)
      //  &&
      //!transformers.isSameSign(diffX2, pins.ballMovement.x)
  {
    pins.player2LostPoint = true;
    /*
    pins.ballPosition = {
      x: pins.ballPosition.x - Math.abs(pins.ballMovement.x),
      y: pins.ballPosition.y + pins.ballMovement.y
    };
    pins.ballMovement = {
      x: -Math.abs(pins.ballMovement.x),
      y: pins.ballMovement.y
    };
    */
  }
  else
  {
    //BOTTOM COLLISION
    //bounce
    var diffY1 = pins.ballPosition.y - pins.walls.y1;
    if(Math.abs(diffY1) < pins.ballRadius ||  pins.ballPosition.y < pins.walls.y1)
        //  &&
        //!transformers.isSameSign(diffY1, pins.ballMovement.x)
    {
      pins.ballPosition = {
          x: pins.ballPosition.x + pins.ballMovement.x,
          y: pins.ballPosition.y + Math.abs(pins.ballMovement.y)
      };
      pins.ballMovement = {
          x: pins.ballMovement.x,
          y: Math.abs(pins.ballMovement.y)
      };
    }
    else
    {
      //TOP COLLISION
      //bounce
      var diffY2 = pins.ballPosition.y - pins.walls.y2;
      if(Math.abs(diffY2) < pins.ballRadius || pins.ballPosition.y > pins.walls.y2)
        //  &&
        //!transformers.isSameSign(diffY2, pins.ballMovement.x)
      {
        pins.ballPosition = {
          x: pins.ballPosition.x + pins.ballMovement.x,
          y: pins.ballPosition.y - Math.abs(pins.ballMovement.y)
        };
        pins.ballMovement = {
          x: pins.ballMovement.x,
          y: -Math.abs(pins.ballMovement.y)
        };
      }
      else
      {
        //test collision with players
        var collidePlayer1 = transformers.collideWithPlayer(
          pins.players.player1,
          pins.ballPosition,
          pins.ballMovement,
          pins.ballRadius
          );
        if(collidePlayer1!== null)
        {
          pins.ballPosition = {x: collidePlayer1.position.x, y: collidePlayer1.position.y};
          pins.ballMovement = {x: collidePlayer1.movement.x, y: collidePlayer1.movement.y};
        }
        else
        {
          var collidePlayer2 = transformers.collideWithPlayer(
          pins.players.player2,
          pins.ballPosition,
          pins.ballMovement,
          pins.ballRadius
          );
          if(collidePlayer2!== null)
          {
            pins.ballPosition = {x: collidePlayer2.position.x, y: collidePlayer2.position.y};
            pins.ballMovement = {x: collidePlayer2.movement.x, y: collidePlayer2.movement.y};
          }
          else
          {
            //normal update
            var moved = transformers.vectorAdd(pins.ballPosition, pins.ballMovement);
            pins.ballPosition = {x: moved.x, y: moved.y};
            if(Math.abs(pins.ballMovement.y) > pins.ballSpeed.max)
            {
              pins.ballMovement = {x: pins.ballMovement.x, y: pins.ballMovement.y*.95}
            }
            else if(Math.abs(pins.ballMovement.y) < pins.ballSpeed.min)
            {
              pins.ballMovement = {x: pins.ballMovement.x, y: pins.ballMovement.y*1.05}
            }
          }
        }
      }
    }
  }
}

PrepareForNextPoint.update:
//load values from memory
var player1 = pins.startValues.players.player1;
var player2 = pins.startValues.players.player2;
var ball = pins.startValues.ball;

pins.player1Position = {x: player1.position.x, y: player1.position.y};
pins.player2Position = {x: player2.position.x, y: player2.position.y};
pins.ballPosition = {x: ball.position.x, y: ball.position.y};
pins.ballMovement = {x: ball.movement.x, y: ball.movement.y};

var player1LostGame = false;
var player2LostGame = false;

///////////////////////////
// PREPARE FOR NEXT GAME //
///////////////////////////
if (pins.player1LostGame || pins.player2LostGame)
{
  pins.player1Score = 0;
  pins.player2Score = 0;
}
else
{
  ////////////////////////////
  // PREPARE FOR NEXT POINT //
  ////////////////////////////
  if(pins.player1LostPoint)
  {
    var newScore = pins.player2Score + 1;
    pins.player2Score = newScore;
    player1LostGame = (newScore >= pins.maxScore);
  }
  else
  {
    if(pins.player2LostPoint)
    {
      var newScore = pins.player1Score + 1;
      pins.player1Score = newScore;
      player2LostGame = (newScore >= pins.maxScore);
    }
  }
}

pins.starting = false;
pins.player1LostPoint = false;
pins.player2LostPoint = false;
pins.player1LostGame = player1LostGame;
pins.player2LostGame = player2LostGame;

