calculateRotationAngle.body:
var h = [
    mousePosition[0] - center[0],
    mousePosition[1] - center[1]
  ];
var omDistance = Math.sqrt(h[0] * h[0] + h[1] * h[1]);
var ratio = -h[1] / omDistance;
var angle = 0;
if (omDistance !== 0) {
  var absValueAngle = Math.acos(ratio) * 180 / Math.PI;
  if (h[0] <= 0) {
    angle = -absValueAngle;
  } else {
    angle = absValueAngle;
  }
}
return angle;

