shapesIntersect.body:
function makeSatObject(shape) {
    switch(shape.type) {
        case "circle":
            return new SAT.Circle(new SAT.Vector(shape.position[0], shape.position[1]), shape.radius);
        case "rectangle":
            return new SAT.Box(new SAT.Vector(shape.position[0], shape.position[1]), shape.size[0], shape.size[1]).toPolygon();
        case "image":
            var img = assets[shape.asset];
            return new SAT.Box(new SAT.Vector(shape.position[0], shape.position[1]), img.naturalWidth, img.naturalHeight).toPolygon();
        default:
            throw new Error("Cannot check intersection for shape of type: " + shape.type);
    }
}

function testIntersection(left, right) {
    var leftSat = makeSatObject(left);
    var rightSat = makeSatObject(right); 

    if(leftSat instanceof SAT.Circle && rightSat instanceof SAT.Circle) {
        return SAT.testCircleCircle(leftSat, rightSat);
    }

    if (leftSat instanceof SAT.Circle && rightSat instanceof SAT.Polygon) {
        return SAT.testCirclePolygon(leftSat, rightSat);
    } 

    if (leftSat instanceof SAT.Polygon && rightSat instanceof SAT.Circle) {
        return SAT.testPolygonCircle(leftSat, rightSat);
    } 

    if (leftSat instanceof SAT.Polygon && rightSat instanceof SAT.Polygon) {
        return SAT.testPolygonPolygon(leftSat, rightSat);
    }

    throw new Error("Cannot test intersections between shapes of types " + left.type + " and " + right.type);
}

// Convert to arrays if necessary
var leftList = _.isArray(left) ? left : [left];
var rightList = _.isArray(right) ? right : [right];

// Check all intersections 
for(var i = 0; i < leftList.length; i++) {
    for(var j = 0; j < rightList.length; j++) {
        if(testIntersection(leftList[i], rightList[j])) return [leftList[i], rightList[j]];  
    }
}
return null; // No intersection

