collideWithPlayer.body:
//test X & Y collisions
  var diffX = player.position.x - ballPosition.x;
  var diffY = player.position.y - ballPosition.y;
  if (
      (Math.abs(diffX) < ballRadius)
      && (Math.abs(diffY) < ballRadius + player.height)
      && !transformers.isSameSign(diffX, ballMovement)
     )
  {
    return {
      position: {
        x: ballPosition.x - ballMovement.x,
        y: ballPosition.y + ballMovement.y
      },
      movement: {
          x: -ballMovement.x,
          y: ballMovement.y - diffY/2
      }
    };
  }
  else
  {
    return null;
  }
