changeParameterThroughKeyboard.update:
for (var keyCode in params.keyMap) {
  if (params.keysDown[keyCode]) {
    var value = params.keyMap[keyCode];
    if (_.isString(value) && value.length > 0 && (value[0] == '+' || value[0] == '-')) {
      params.parameter += Number(value);
    } else {
      params.parameter = value;
    }
    break;
  }
}

doInSequence.handleSignals:
if (signals[params.activeChild] == GE.signals.DONE)
  params.activeChild++;
if (params.activeChild > children.length - 1) {
  params.activeChild = 0;
  return GE.signals.DONE;
}

sandwich.listActiveChildren:
if (!params.track) {
  if (!params.condition) {
    return [0];
  } else {
    params.track = true;
    return [1];
  }
} else {
  if (params.condition) {
    return [1];
  } else {
    params.track = false;
    return [2];
  }
}

detectMouse.update:
if (!params.state)
  params.state = 'none';
switch (params.state) {
case 'none':
  if (params.mousePosition) {
    for (var i in params.shapes) {
      if (tools.pointIntersectsShape(params.mousePosition, params.shapes[i])) {
        log(GE.logLevels.INFO, 'Entering hover mode. Old state = ' + params.state);
        params.state = 'hover';
        params.shape = params.shapes[i];
        break;
      }
    }
  }
  break;
case 'hover':
  if (!params.mousePosition || !tools.pointIntersectsShape(params.mousePosition, params.shape)) {
    params.state = 'none';
    params.shape = null;
    log(GE.logLevels.INFO, 'Leaving hover mode');
  } else if (params.mouseDown) {
    params.dragStartPosition = params.mousePosition;
    params.state = 'pressed';
    log(GE.logLevels.INFO, 'Entering presed mode');
  }
  break;
case 'pressed':
  if (!params.mouseDown) {
    params.state = 'click';
    params.dragStartPosition = null;
    log(GE.logLevels.INFO, 'Leaving pressed mode');
  } else if (Vector.create(params.dragStartPosition).distanceFrom(Vector.create(params.mousePosition)) >= params.minimumDragDistance) {
    params.state = 'startDrag';
    params.dragStartPosition = null;
    log(GE.logLevels.INFO, 'Entering drag mode');
  }
  break;
case 'click':
  params.state = 'hover';
  break;
case 'startDrag':
  params.state = 'drag';
  break;
case 'drag':
  if (!params.mouseDown) {
    params.state = 'endDrag';
    params.dragStartPosition = null;
    log(GE.logLevels.INFO, 'Leaving drag mode');
  }
  break;
case 'endDrag':
  params.state = 'hover';
  break;
default:
  throw new Error('Unknown state \'' + params.state + '\'');
}

updateHtmlTemplate.update:
if (!params.htmlService.receive[params.templateName]) {
  params.htmlService.send[params.templateName] = {
    asset: params.assetName,
    values: params.modelValues
  };
} else {
  params.modelValues = params.htmlService.receive[params.templateName].values;
  params.htmlService.send[params.templateName] = params.htmlService.receive[params.templateName];
}

drawShape.body:
var shapes = oldShapes || {};
shapes[_.uniqueId()] = shape;
return shapes;

pointIntersectsShape.body:
if (!shape.fillStyle && !shape.strokeStyle)
  return false;
switch (shape.type) {
case 'circle':
  var center = Vector.create(shape.center);
  if (shape.translation)
    center = center.add(shape.translation);
  var lineWidth = shape.lineWidth || 1;
  var scale = shape.scale || 1;
  var minDistance = shape.fillStyle ? 0 : shape.radius - lineWidth;
  var maxDistance = shape.strokeStyle ? shape.radius + lineWidth : shape.radius;
  var distance = center.distanceFrom(Vector.create(point));
  return distance >= minDistance * scale && distance <= maxDistance * scale;
case 'rectangle':
  var pointInShapeFrame = Vector.create(point);
  if (shape.translation)
    pointInShapeFrame = pointInShapeFrame.subtract(shape.translation);
  return pointInShapeFrame.elements[0] >= shape.position[0] && pointInShapeFrame.elements[0] <= shape.position[0] + shape.size[0] && pointInShapeFrame.elements[1] >= shape.position[1] && pointInShapeFrame.elements[1] <= shape.position[1] + shape.size[1];
default:
  throw new Error('Shape type \'' + shape.type + '\' is not supported');
}

removeElement.body:
var newArray = [];
for (var i = 0; i < tab.length; i++) {
  if (i != index)
    newArray.push(tab[i]);
}
return newArray;

gridCellAtPoint.body:
if (point === null)
  return null;
var gridPos = [
    Math.floor((point[0] - grid.upperLeft[0]) / grid.cellSize[0]),
    Math.floor((point[1] - grid.upperLeft[1]) / grid.cellSize[1])
  ];
if (grid.type != 'infinite' && (gridPos[0] < 0 || gridPos[0] > grid.gridSize[0] || gridPos[1] < 0 || gridPos[1] > grid.gridSize[1]))
  return null;
else
  return gridPos;

gridCellToPoint.body:
return [
  (cell[0] + proportions[0]) * grid.cellSize[0] + grid.upperLeft[0],
  (cell[1] + proportions[1]) * grid.cellSize[1] + grid.upperLeft[1]
];

gridCellRectangle.body:
return {
  type: 'rectangle',
  position: tools.gridCellUpperLeft(grid, cell),
  size: grid.cellSize,
  meta: meta
};

calculateRotationAngle.body:
var h = [
    mousePosition[0] - center[0],
    mousePosition[1] - center[1]
  ];
var omDistance = Math.sqrt(h[0] * h[0] + h[1] * h[1]);
var ratio = -h[1] / omDistance;
var angle = 0;
if (omDistance !== 0) {
  var absValueAngle = Math.acos(ratio) * 180 / Math.PI;
  if (h[0] <= 0) {
    angle = -absValueAngle;
  } else {
    angle = absValueAngle;
  }
}
return angle;

makeGridCoveringRectangle.body:
return {
  meta: meta,
  type: 'rectangle',
  position: grid.upperLeft,
  size: tools.gridSizeInPixels(grid),
  strokeStyle: 'white',
  fillStyle: 'white'
};

meanOfCoordinates.body:
var sum = _.reduce(coordinates, function (memo, cell) {
    return [
      memo[0] + cell[0],
      memo[1] + cell[1]
    ];
  }, [
    0,
    0
  ]);
return [
  sum[0] / coordinates.length,
  sum[1] / coordinates.length
];

findCenterOfCells.body:
if (cells.length == 0)
  return [
    0,
    0
  ];
var gridCenter = tools.gridCenter(grid);
var sum = _.reduce(cells, function (memo, cell) {
    return [
      memo[0] + cell[0] + 0.5,
      memo[1] + cell[1] + 0.5
    ];
  }, [
    0,
    0
  ]);
return [
  sum[0] * grid.cellSize[0] / cells.length - gridCenter[0],
  sum[1] * grid.cellSize[1] / cells.length - gridCenter[1]
];

makeBlockShapes.body:
var that = tools;
return _.map(blocks, function (block) {
  return _.extend(that.gridCellRectangle(grid, block, block), {
    layer: 'blocks',
    fillStyle: blockColor,
    size: blockSize
  });
});

makeMovableBlockShapes.body:
var that = tools;
var movableBlocks = _.filter(blocks, function (block) {
    return that.canMoveBlock(blocks, block);
  });
return _.map(movableBlocks, function (block) {
  return _.extend(that.gridCellRectangle(grid, block, block), {
    layer: 'blocks',
    fillStyle: blockColor,
    size: blockSize
  });
});

makeDraggedShape.body:
return {
  layer: 'drag',
  type: 'rectangle',
  position: [
    mousePosition[0] - size[0] / 2,
    mousePosition[1] - size[1] / 2
  ],
  size: size,
  fillStyle: color
};

drawShapes.body:
var that = tools;
return _.reduce(shapes, function (memo, shape) {
  return that.drawShape(shape, memo);
}, {});

blocksAreNeighbors.body:
var diff = [
    Math.abs(a[0] - b[0]),
    Math.abs(a[1] - b[1])
  ];
return diff[0] == 1 && diff[1] == 0 || diff[0] == 0 && diff[1] == 1;

makeAdjacencyList.body:
var adjList = _.map(blocks, function () {
    return [];
  });
for (var i = 0; i < blocks.length - 1; i++) {
  for (var j = i + 1; j < blocks.length; j++) {
    if (tools.blocksAreNeighbors(blocks[i], blocks[j])) {
      adjList[i].push(j);
      adjList[j].push(i);
    }
  }
}
return adjList;

visitBlocks.body:
var visited = [startingIndices];
while (true) {
  var toVisit = _.reduce(visited[visited.length - 1], function (memo, visitingIndex) {
      return memo.concat(adjList[visitingIndex]);
    }, []);
  toVisit = _.uniq(toVisit);
  toVisit = _.difference.apply(_, [toVisit].concat(visited));
  if (toVisit.length > 0) {
    visited.push(toVisit);
  } else {
    return visited;
  }
}

canMoveBlock.body:
var blocksWithout = tools.removeElement(blocks, GE.indexOf(blocks, block));
var adjList = tools.makeAdjacencyList(blocksWithout);
var visited = tools.visitBlocks(adjList, [0]);
return _.flatten(visited).length == blocksWithout.length;

findFreeBlockPositions.body:
var freePositions = [];
_.each(blocks, function (block) {
  freePositions.push([
    block[0] + 1,
    block[1]
  ]);
  freePositions.push([
    block[0] - 1,
    block[1]
  ]);
  freePositions.push([
    block[0],
    block[1] + 1
  ]);
  freePositions.push([
    block[0],
    block[1] - 1
  ]);
});
freePositions = GE.uniq(freePositions);
return GE.difference(freePositions, blocks);

translateShapes.body:
var newShapes = _.map(shapes, function (shape) {
    return _.extend(shape, {
      position: [
        shape.position[0] + translation[0],
        shape.position[1] + translation[1]
      ]
    });
  });
return newShapes;

makeSelectionGalleryGrids.body:
var that = tools;
return _.map(_.range(gallery.length), function (index) {
  var cell = that.gridIndexToCell(selectionGrid, index);
  return {
    type: 'infinite',
    'upperLeft': that.gridCellUpperLeft(selectionGrid, cell),
    'cellSize': galleryGrid.cellSize,
    'gridSize': galleryGrid.gridSize
  };
});

makeSelectionGalleryShapes.body:
var that = tools;
return _.map(selectionGrids, function (selectionGrid, index) {
  return _.extend(that.makeGridCoveringRectangle(selectionGrid, index), {
    fillStyle: GE.contains(selectedGalleryIndexes, index) ? 'red' : 'grey',
    strokeStyle: 'white',
    layer: 'gallery'
  });
});

serializeBlocks.body:
var min = _.reduce(blocks, function (memo, block) {
    return [
      Math.min(memo[0], block[0]),
      Math.min(memo[1], block[1])
    ];
  }, [
    Infinity,
    Infinity
  ]);
var rowNumbers = _.map(_.range(10), function () {
    return 0;
  });
_.each(blocks, function (block) {
  var index = block[1] - min[1];
  rowNumbers[index] = rowNumbers[index] | 1 << 9 - (block[0] - min[0]);
});
return rowNumbers;

findDroppedPosition.body:
var that = tools;
var hoveredCell = tools.gridCellAtPoint(grid, mousePosition);
if (!hoveredCell)
  return blocks;
var freeBlockPositions = tools.findFreeBlockPositions(blocks);
return _.min(freeBlockPositions, function (block) {
  return that.distanceBetweenPoints(hoveredCell, block);
});

makeBlockGroupsToBeHighlighted.body:
log(GE.logLevels.INFO, 'makeBlockGroupsToBeHighlighted', arguments);
var newBlocks = GE.appendToArray(blocks, newPosition);
var adjList = tools.makeAdjacencyList(newBlocks);
var visitedBlockIndexes = _.rest(tools.visitBlocks(adjList, [newBlocks.length - 1]));
return _.map(visitedBlockIndexes, function (blockIndexes) {
  return _.map(blockIndexes, function (index) {
    return newBlocks[index];
  });
});

adjustShapesByMeta.body:
return _.map(shapes, function (shape) {
  if (_.isEqual(shape.meta, meta)) {
    return _.extend(GE.clone(shape), properties);
  } else {
    return shape;
  }
});

interpolateVector.body:
return _.map(_.zip(start, end), function (pair) {
  return pair[0] + fraction * (pair[1] - pair[0]);
});

rgbColorString.body:
